// GetRandomParagraph returns a random paragraph from the list of paragraphs
func GetRandomParagraph() string {
	return fmt.Sprintf(randomParagraphs[rand.Intn(len(randomParagraphs))])
}

// GetRandomParagraphs returns a slice of random paragraphs from the list of paragraphs
func GetRandomParagraphs(n int) []string {
	paragraphs := make([]string, n)
	for i := 0; i < n; i++ {
		paragraphs[i] = GetRandomParagraph()
	}
	return paragraphs
}

func GetRandomWord() string {
	// we get a random paragraph and then
	// split it into words, then we return
	// a random word from that list
	paragraph := GetRandomParagraph()
	words := strings.Split(paragraph, " ")
	return words[rand.Intn(len(words))]
}
